mapper.update:
UPDATE TOP
SET TOTAL = #{total}, SHOULDER = #{shoulder}, CHEST = #{chest}, SLEEVE = #{sleeve}
WHERE USERID = #{userId}

UPDATE PANTS
SET TOTAL = #{total}, WAIST = #{waist}, THIGH = #{thigh}, RISE = #{rise}, HEM = #{hem}
WHERE USERID = #{userId}

mapper.select:
SELECT COUNT(*)
FROM QUESTION Q
WHERE USERID = #{userId}
